mapper.select:
	    select mname, mid, mphone, mins
	    from don_member
	    where mid=#{mid} 
        and mpwd=#{mpwd}

select mid, mname, mpwd, mphone 
from don_member
where mid = #{mid}

select count(mid)
from don_member
where mid = #{mid}

mapper.update:
 
		update don_member
		set mname = #{mname}, mpwd = #{mpwd}, mphone = #{mphone}
		where mid = #{mid}
